import { Router } from 'express';
import { BookController } from '../controllers/bookController';

const router = Router();

// Define book routes
router.get('/', BookController.getBooks);
router.get('/:id', BookController.getBookById);

// Export the router
export { router as booksRouter };

import fs from 'fs';
import path from 'path';
import { v4 as uuidv4 } from 'uuid';
import { Book } from './interfaces/book.interface';

// Path to book data directory
const BOOKS_DIR = path.join(__dirname, '../../data/books');
// Path to indexes directory
const INDEXES_DIR = path.join(__dirname, '../../data/indexes');

  /**
   * Get a book by ID
   * @param id Book ID
   * @returns Book data or null if not found
   */
  static async getBookById(id: string): Promise<Book | null> {
    try {
      const filePath = path.join(BOOKS_DIR, `${id}.json`);
      
      if (!fs.existsSync(filePath)) {
        return null;
      }
      
      const fileContent = await fs.promises.readFile(filePath, 'utf-8');
      return JSON.parse(fileContent) as Book;
    } catch (error) {
      console.error(`Error fetching book with ID ${id}:`, error);
      throw error;
    }
  }
  
  /**
   * Search for books by title or author
   * @param query Search query
   * @param limit Maximum number of results
   * @returns Array of matching books
   */
  static async searchBooks(query: string, limit: number = 10): Promise<Book[]> {
    try {
      // Ensure books directory exists
      if (!fs.existsSync(BOOKS_DIR)) {
        fs.mkdirSync(BOOKS_DIR, { recursive: true });
        return [];
      }
      
      // Read all book files
      const fileNames = fs.readdirSync(BOOKS_DIR);
      const bookPromises = fileNames.map(async (fileName) => {
        const filePath = path.join(BOOKS_DIR, fileName);
        const fileContent = await fs.promises.readFile(filePath, 'utf-8');
        return JSON.parse(fileContent) as Book;
      });
      
      // Resolve all book data
      const books = await Promise.all(bookPromises);
      
      // Normalize query for case-insensitive search
      const normalizedQuery = query.toLowerCase();
      
      // Filter books based on title or author
      const filteredBooks = books.filter(book => {
        const titleMatch = book.title.toLowerCase().includes(normalizedQuery);
        const authorMatch = book.author.toLowerCase().includes(normalizedQuery);
        return titleMatch || authorMatch;
      });
      
      // Return limited results
      return filteredBooks.slice(0, limit);
    } catch (error) {
      console.error('Error searching books:', error);
      throw error;
    }
  }
  
  /**
   * Get autocomplete suggestions based on query
   * @param query Search query
   * @param limit Maximum number of suggestions
   * @returns Array of suggestions (title or author)
   */
  static async getSuggestions(query: string, limit: number = 5): Promise<string[]> {
    try {
      const books = await this.searchBooks(query, 20); // Get a larger set to extract suggestions from
      
      // Extract titles and authors that match the query
      const titleSuggestions = books
        .filter(book => book.title.toLowerCase().includes(query.toLowerCase()))
        .map(book => book.title);
      
      const authorSuggestions = books
        .filter(book => book.author.toLowerCase().includes(query.toLowerCase()))
        .map(book => book.author);
      
      // Combine unique suggestions
      const allSuggestions = [...new Set([...titleSuggestions, ...authorSuggestions])];
      
      // Return limited results
      return allSuggestions.slice(0, limit);
    } catch (error) {
      console.error('Error getting suggestions:', error);
      throw error;
    }
  }
}

  /**
   * Get a book by ID
   * @param req Express request object
   * @param res Express response object
   */
  static async getBookById(req: Request, res: Response) {
    try {
      const { id } = req.params;

      if (!id) {
        return res.status(400).json({
          status: 'error',
          error: { code: 400, message: 'Book ID is required' }
        });
      }

      const book = await BookModel.getBookById(id);

      if (!book) {
        return res.status(404).json({
          status: 'error',
          error: { code: 404, message: 'Book not found' }
        });
      }

      return res.status(200).json({
        status: 'success',
        data: book,
        error: null
      });
    } catch (error) {
      console.error(`Error in getBookById controller:`, error);
      return res.status(500).json({
        status: 'error',
        data: null,
        error: { code: 500, message: 'Failed to fetch book details' }
      });
    }
  }

  /**
   * Search for books by title or author
   * @param req Express request object
   * @param res Express response object
   */
  static async searchBooks(req: Request, res: Response) {
    try {
      const query = req.query.q as string;
      const limit = parseInt(req.query.limit as string) || 10;

      if (!query || query.trim() === '') {
        return res.status(400).json({
          status: 'error',
          error: { code: 400, message: 'Search query is required' }
        });
      }

      const books = await BookModel.searchBooks(query, limit);

      return res.status(200).json({
        status: 'success',
        data: books,
        error: null
      });
    } catch (error) {
      console.error('Error in searchBooks controller:', error);
      return res.status(500).json({
        status: 'error',
        data: null,
        error: { code: 500, message: 'Failed to search books' }
      });
    }
  }

  /**
   * Get autocomplete suggestions based on query
   * @param req Express request object
   * @param res Express response object
   */
  static async getSuggestions(req: Request, res: Response) {
    try {
      const query = req.query.q as string;
      const limit = parseInt(req.query.limit as string) || 5;

      if (!query || query.trim() === '') {
        return res.status(200).json({
          status: 'success',
          data: [],
          error: null
        });
      }

      const suggestions = await BookModel.getSuggestions(query, limit);

      return res.status(200).json({
        status: 'success',
        data: suggestions,
        error: null
      });
    } catch (error) {
      console.error('Error in getSuggestions controller:', error);
      return res.status(500).json({
        status: 'error',
        data: null,
        error: { code: 500, message: 'Failed to get suggestions' }
      });
    }
  }
}

// src/routes/books.ts
import { Router } from 'express';
import { BookController } from '../controllers/bookController';

const router = Router();

// Define book routes
router.get('/', BookController.getBooks);
router.get('/search', BookController.searchBooks);
router.get('/suggestions', BookController.getSuggestions);
router.get('/:id', BookController.getBookById);

// Export the router
export { router as booksRouter };

  /**
   * Get a book by ID
   * @param id Book ID
   * @returns Promise with book data
   */
  async getBookById(id: string): Promise<Book> {
    try {
      const response = await axios.get(`${API_URL}/books/${id}`);
      return response.data.data;
    } catch (error) {
      console.error(`Error fetching book with ID ${id}:`, error);
      throw error;
    }
  },

  /**
   * Search for books by title or author
   * @param query Search query
   * @param limit Maximum number of results
   * @returns Promise with book data
   */
  async searchBooks(query: string, limit: number = 10): Promise<Book[]> {
    try {
      const response = await axios.get(`${API_URL}/books/search`, {
        params: { q: query, limit }
      });
      return response.data.data;
    } catch (error) {
      console.error('Error searching books:', error);
      throw error;
    }
  },

  /**
   * Get autocomplete suggestions based on query
   * @param query Search query
   * @param limit Maximum number of suggestions
   * @returns Promise with suggestions
   */
  async getSuggestions(query: string, limit: number = 5): Promise<string[]> {
    try {
      const response = await axios.get(`${API_URL}/books/suggestions`, {
        params: { q: query, limit }
      });
      return response.data.data;
    } catch (error) {
      console.error('Error getting suggestions:', error);
      throw error;
    }
  }
};

