{"content": "import axios from 'axios';

const API_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:3001/api/v1';

/**
 * Interface for book data
 */
export interface Book {
  id: string;
  title: string;
  author: string;
  description: string;
  coverImage: string;
  genres: string[];
  publishedYear: number;
  createdAt: string;
  updatedAt: string;
  averageRating?: number;
  reviewCount?: number;
}

/**
 * Interface for paginated book response
 */
export interface PaginatedBooks {
  books: Book[];
  total: number;
  page: number;
  limit: number;
  totalPages: number;
}

/**
 * Service for book-related API calls
 */
export const BookService = {
  /**
   * Get a paginated list of books
   * @param page Page number (1-based)
   * @param limit Number of items per page
   * @param sortBy Field to sort by
   * @param sortOrder Sort direction ('asc' or 'desc')
   * @returns Promise with paginated book data
   */
  async getBooks(
    page: number = 1, 
    limit: number = 10, 
    sortBy: string = 'title', 
    sortOrder: string = 'asc'
  ): Promise<PaginatedBooks> {
    try {
      const response = await axios.get(`${API_URL}/books`, {
        params: { page, limit, sortBy, sortOrder }
      });

      return response.data.data;
    } catch (error) {
      console.error('Error fetching books:', error);
      throw error;
    }
  },

  /**
   * Get a book by ID
   * @param id Book ID
   * @returns Promise with book data
   */
  async getBookById(id: string): Promise<Book> {
    try {
      const response = await axios.get(`${API_URL}/books/${id}`);
      return response.data.data;
    } catch (error) {
      console.error(`Error fetching book with ID ${id}:`, error);
      throw error;
    }
  }
};", "filePath": "d:\app\bookreviewapp\frontend\src\services\bookService.ts"}

{"content": "import React from 'react';
import { 
  Card, 
  CardContent, 
  CardMedia, 
  Typography, 
  Rating,
  Box,
  Chip,
  CardActionArea
} from '@mui/material';
import { useRouter } from 'next/router';
import { Book } from '../../services/bookService';

interface BookCardProps {
  book: Book;
}

/**
 * Component for displaying a book card in the book list
 */
const BookCard: React.FC<BookCardProps> = ({ book }) => {
  const router = useRouter();

  // Truncate description to a reasonable length
  const truncateText = (text: string, maxLength: number = 100) => {
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength) + '...';
  };

  // Handle click to navigate to book details
  const handleClick = () => {
    router.push(`/books/${book.id}`);
  };

  return (
    <Card 
      sx={{ 
        height: '100%', 
        display: 'flex', 
        flexDirection: 'column',
        transition: 'transform 0.2s',
        '&:hover': {
          transform: 'scale(1.02)',
          boxShadow: 3
        }
      }}
    >
      <CardActionArea onClick={handleClick}>
        <CardMedia
          component="img"
          height="200"
          image={book.coverImage || '/images/book-placeholder.jpg'}
          alt={book.title}
          sx={{ objectFit: 'contain', padding: 1 }}
        />
        <CardContent sx={{ flexGrow: 1 }}>
          <Typography gutterBottom variant="h6" component="div" noWrap>
            {book.title}
          </Typography>
          <Typography variant="body2" color="text.secondary" gutterBottom>
            {book.author}
          </Typography>

          {/* Display rating if available */}
          {book.averageRating !== undefined && (
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <Rating 
                value={book.averageRating} 
                precision={0.1} 
                readOnly 
                size="small"
              />
              <Typography variant="body2" color="text.secondary" ml={1}>
                ({book.averageRating.toFixed(1)})
                {book.reviewCount !== undefined && `  ${book.reviewCount} reviews`}
              </Typography>
            </Box>
          )}

          {/* Display genres */}
          <Box sx={{ mt: 1 }}>
            {book.genres?.slice(0, 2).map((genre, index) => (
              <Chip 
                key={index}
                label={genre}
                size="small"
                variant="outlined"
                sx={{ mr: 0.5, mb: 0.5 }}
              />
            ))}
            {book.genres?.length > 2 && (
              <Chip 
                label={`+${book.genres.length - 2}`}
                size="small"
                variant="outlined"
                sx={{ mb: 0.5 }}
              />
            )}
          </Box>
        </CardContent>
      </CardActionArea>
    </Card>
  );
};

export default BookCard;", "filePath": "d:\app\bookreviewapp\frontend\src\components\books\BookCard.tsx"}

{"content": "import React, { useState, useEffect } from 'react';
import { 
  Grid, 
  Box, 
  Pagination, 
  FormControl, 
  MenuItem, 
  Select, 
  Typography, 
  Container,
  Skeleton,
  Alert,
  SelectChangeEvent
} from '@mui/material';
import { useRouter } from 'next/router';
import { BookService, PaginatedBooks } from '../../services/bookService';
import BookCard from './BookCard';

/**
 * Component for displaying a paginated list of books
 */
const BookList: React.FC = () => {
  // State
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [bookData, setBookData] = useState<PaginatedBooks | null>(null);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(12);
  const [sortBy, setSortBy] = useState('title');
  const [sortOrder, setSortOrder] = useState('asc');

  const router = useRouter();

  // Effect to fetch books when parameters change
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        setLoading(true);
        setError(null);
        const data = await BookService.getBooks(page, pageSize, sortBy, sortOrder);
        setBookData(data);
      } catch (err) {
        setError('Failed to load books. Please try again later.');
        console.error('Error loading books:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, [page, pageSize, sortBy, sortOrder]);

  // Update URL with query parameters
  useEffect(() => {
    router.replace({
      pathname: router.pathname,
      query: { page, pageSize, sortBy, sortOrder }
    }, undefined, { shallow: true });
  }, [page, pageSize, sortBy, sortOrder, router]);

  // Event handlers
  const handlePageChange = (_event: React.ChangeEvent<unknown>, value: number) => {
    setPage(value);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handlePageSizeChange = (event: SelectChangeEvent<number>) => {
    setPageSize(event.target.value as number);
    setPage(1); // Reset to first page when changing page size
  };

  const handleSortByChange = (event: SelectChangeEvent) => {
    setSortBy(event.target.value);
    setPage(1);
  };

  const handleSortOrderChange = (event: SelectChangeEvent) => {
    setSortOrder(event.target.value);
    setPage(1);
  };

  // Render loading skeletons
  const renderSkeletons = () => {
    return Array(pageSize).fill(0).map((_, index) => (
      <Grid item xs={12} sm={6} md={4} lg={3} key={`skeleton-${index}`}>
        <Box sx={{ width: '100%', height: '100%' }}>
          <Skeleton variant="rectangular" height={200} />
          <Box sx={{ pt: 0.5 }}>
            <Skeleton />
            <Skeleton width="60%" />
          </Box>
        </Box>
      </Grid>
    ));
  };

  return (
    <Container maxWidth="xl">
      {/* Error message */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Filters and sort options */}
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        flexWrap: 'wrap', 
        mb: 3 
      }}>
        <Typography variant="h4" component="h1" sx={{ mb: { xs: 2, sm: 0 } }}>
          Books
        </Typography>

        <Box sx={{ 
          display: 'flex',
          gap: 2,
          flexWrap: 'wrap' 
        }}>
          {/* Sort by */}
          <FormControl size="small" sx={{ minWidth: 120 }}>
            <Select
              value={sortBy}
              onChange={handleSortByChange}
              displayEmpty
              variant="outlined"
            >
              <MenuItem value="title">Title</MenuItem>
              <MenuItem value="author">Author</MenuItem>
              <MenuItem value="publishedYear">Published Year</MenuItem>
              {/* Add more sort options here */}
            </Select>
          </FormControl>

          {/* Sort order */}
          <FormControl size="small" sx={{ minWidth: 120 }}>
            <Select
              value={sortOrder}
              onChange={handleSortOrderChange}
              displayEmpty
              variant="outlined"
            >
              <MenuItem value="asc">Ascending</MenuItem>
              <MenuItem value="desc">Descending</MenuItem>
            </Select>
          </FormControl>

          {/* Page size */}
          <FormControl size="small" sx={{ minWidth: 80 }}>
            <Select
              value={pageSize}
              onChange={handlePageSizeChange}
              displayEmpty
              variant="outlined"
            >
              <MenuItem value={12}>12</MenuItem>
              <MenuItem value={24}>24</MenuItem>
              <MenuItem value={48}>48</MenuItem>
            </Select>
          </FormControl>
        </Box>
      </Box>

      {/* Book grid */}
      <Grid container spacing={3}>
        {loading ? (
          renderSkeletons()
        ) : bookData?.books && bookData.books.length > 0 ? (
          bookData.books.map(book => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={book.id}>
              <BookCard book={book} />
            </Grid>
          ))
        ) : (
          <Grid item xs={12}>
            <Box sx={{ textAlign: 'center', py: 5 }}>
              <Typography variant="h6">
                No books found.
              </Typography>
            </Box>
          </Grid>
        )}
      </Grid>

      {/* Pagination */}
      {bookData && bookData.totalPages > 1 && (
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'center', 
          mt: 4, 
          mb: 2 
        }}>
          <Pagination
            count={bookData.totalPages}
            page={page}
            onChange={handlePageChange}
            size="large"
            color="primary"
            showFirstButton
            showLastButton
          />
        </Box>
      )}
    </Container>
  );
};

