  /**
   * Create a new book
   * @param bookData Book data without ID
   * @returns Created book with ID
   */
  static async createBook(bookData: Omit<Book, 'id' | 'createdAt' | 'updatedAt'>): Promise<Book> {
    try {
      // Ensure books directory exists
      if (!fs.existsSync(BOOKS_DIR)) {
        fs.mkdirSync(BOOKS_DIR, { recursive: true });
      }
      
      // Generate ID and timestamps
      const id = uuidv4();
      const now = new Date();
      
      const newBook: Book = {
        ...bookData,
        id,
        createdAt: now,
        updatedAt: now
      };
      
      // Write to file
      const filePath = path.join(BOOKS_DIR, `${id}.json`);
      await fs.promises.writeFile(filePath, JSON.stringify(newBook, null, 2));
      
      return newBook;
    } catch (error) {
      console.error('Error creating book:', error);
      throw error;
    }
  }

  /**
   * Update an existing book
   * @param id Book ID
   * @param bookData Book data to update
   * @returns Updated book
   */
  static async updateBook(id: string, bookData: Partial<Book>): Promise<Book | null> {
    try {
      const book = await this.getBookById(id);
      
      if (!book) {
        return null;
      }
      
      // Update book data
      const updatedBook: Book = {
        ...book,
        ...bookData,
        id, // Ensure ID doesn't change
        updatedAt: new Date()
      };
      
      // Write to file
      const filePath = path.join(BOOKS_DIR, `${id}.json`);
      await fs.promises.writeFile(filePath, JSON.stringify(updatedBook, null, 2));
      
      return updatedBook;
    } catch (error) {
      console.error(`Error updating book with ID ${id}:`, error);
      throw error;
    }
  }

  /**
   * Delete a book
   * @param id Book ID
   * @returns True if deleted, false if not found
   */
  static async deleteBook(id: string): Promise<boolean> {
    try {
      const filePath = path.join(BOOKS_DIR, `${id}.json`);
      
      if (!fs.existsSync(filePath)) {
        return false;
      }
      
      // Delete file
      await fs.promises.unlink(filePath);
      
      return true;
    } catch (error) {
      console.error(`Error deleting book with ID ${id}:`, error);
      throw error;
    }
  }
}

  /**
   * Get autocomplete suggestions based on query
   * @param req Express request object
   * @param res Express response object
   */
  static async getSuggestions(req: Request, res: Response) {
    try {
      const query = req.query.q as string;
      const limit = parseInt(req.query.limit as string) || 5;

      if (!query || query.trim() === '') {
        return res.status(200).json({
          status: 'success',
          data: [],
          error: null
        });
      }

      const suggestions = await BookModel.getSuggestions(query, limit);

      return res.status(200).json({
        status: 'success',
        data: suggestions,
        error: null
      });
    } catch (error) {
      console.error('Error in getSuggestions controller:', error);
      return res.status(500).json({
        status: 'error',
        data: null,
        error: { code: 500, message: 'Failed to get suggestions' }
      });
    }
  }

  /**
   * Create a new book
   * @param req Express request object
   * @param res Express response object
   */
  static async createBook(req: Request, res: Response) {
    try {
      const { title, author, description, coverImage, genres, publishedYear } = req.body;

      // Validate required fields
      if (!title || !author) {
        return res.status(400).json({
          status: 'error',
          error: { code: 400, message: 'Title and author are required' }
        });
      }

      // Create book
      const newBook = await BookModel.createBook({
        title,
        author,
        description: description || '',
        coverImage: coverImage || '',
        genres: genres || [],
        publishedYear: publishedYear || new Date().getFullYear(),
      });

      return res.status(201).json({
        status: 'success',
        data: newBook,
        error: null
      });
    } catch (error) {
      console.error('Error in createBook controller:', error);
      return res.status(500).json({
        status: 'error',
        data: null,
        error: { code: 500, message: 'Failed to create book' }
      });
    }
  }

  /**
   * Update an existing book
   * @param req Express request object
