import { v4 as uuidv4 } from 'uuid';
import { Book } from '../src/models/interfaces/book.interface';

// Path to book data directory
const BOOKS_DIR = path.join(__dirname, '../data/books');

// Get book count from command line arguments, default to 100
const BOOK_COUNT = process.argv.length > 2 
  ? parseInt(process.argv[2], 10) 
  : 100;

// Validate count
if (isNaN(BOOK_COUNT) || BOOK_COUNT < 1) {
  console.error('Invalid book count. Using default of 100.');
  generateBooks(100);
} else {
  generateBooks(BOOK_COUNT);
}

// src/routes/index.ts
import { Router } from 'express';
import { booksRouter } from './books';
import { usersRouter } from './users';
import { reviewsRouter } from './reviews';
import { authRouter } from './auth';
import { dataRouter } from './data';

const router = Router();

// Mount the routers for different resources
router.use('/auth', authRouter);
router.use('/books', booksRouter);
router.use('/users', usersRouter);
router.use('/reviews', reviewsRouter);
router.use('/data', dataRouter);

export { router as apiRouter };

const API_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:3001/api/v1';

/**
 * Admin component for data generation
 */
const BookDataGenerator: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const [count, setCount] = useState(100);

  const handleGenerateBooks = async () => {
    try {
      setLoading(true);
      setError(null);
      setSuccess(null);
      
      // Call the real API endpoint to generate books
      const response = await fetch(`${API_URL}/data/generate/books?count=${count}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // In a real implementation, we would include an auth token
          // Authorization: `Bearer ${authToken}`
        },
        credentials: 'include' // Include cookies for authentication
      });
      
      const data = await response.json();
      
      if (response.ok) {
        setSuccess(`Successfully generated ${count} books. Refresh the book list to see them.`);
      } else {
        throw new Error(data.error?.message || 'Failed to generate books');
      }
    } catch (err: any) {
      setError(err.message || 'Failed to generate books. Please try again later.');
      console.error('Error generating books:', err);
    } finally {
      setLoading(false);
    }
  };

